import React, { useState, useRef, useEffect } from 'react';

// --- Ikon SVG (Sama seperti sebelumnya) ---
const CarIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" {...props}><path d="M19 17h2c.6 0 1-.4 1-1v-3c0-1.1-.9-2-2-2h-1.3l-1.6-4.4A2 2 0 0 0 15.2 6H8.8a2 2 0 0 0-1.9 1.6L5.3 12H4c-1.1 0-2 .9-2 2v3c0 .6.4 1 1 1h2" /><path d="M19 17h-14" /><circle cx="6.5" cy="17.5" r="2.5" /><circle cx="17.5" cy="17.5" r="2.5" /></svg>
);
const ClipboardIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" {...props}><path d="M16 4h2a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h2" /><rect x="8" y="2" width="8" height="4" rx="1" /></svg>
);
const CheckSquareIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" {...props}><polyline points="9 11 12 14 22 4" /><path d="M21 12v7a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11" /></svg>
);
const UserIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" {...props}><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" /><circle cx="12" cy="7" r="4" /></svg>
);
const ChevronDownIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" {...props}><polyline points="6 9 12 15 18 9" /></svg>
);

// --- Komponen Utama ---

function App() {
  const [activeTab, setActiveTab] = useState('customer'); // 'customer', 'inspection', 'signature'
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [formData, setFormData] = useState({
    customer: {
      location: 'Wrap Station Medan',
      firstName: '',
      lastName: '',
      phone: '',
      carBrand: '',
      carModel: '',
      color: '',
    },
    inspection: {}, // Akan menyimpan { item: { rating: 'G', note: 'bla', file: FileObject } }
    signature: '',
    agreed: false,
  });

  const handleFormChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value,
      },
    }));
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);
    // Di sini Anda akan mengirim `formData` ke backend
    console.log('Mengirim data ke backend:', formData);

    // Filter `formData.inspection` untuk hanya mengirim `fileName` dan `dataUrl` (untuk gambar)
    const inspectionDataToSend = {};
    for (const itemKey in formData.inspection) {
        const itemDetails = formData.inspection[itemKey];
        inspectionDataToSend[itemKey] = {
            rating: itemDetails.rating,
            note: itemDetails.note,
            // Jika ada file, kirim data URL atau nama file
            file: itemDetails.file ? itemDetails.file.dataUrl || itemDetails.file.name : undefined,
            fileName: itemDetails.file ? itemDetails.file.name : undefined
        };
    }

    const dataToSend = {
        ...formData,
        inspection: inspectionDataToSend
    };


    // Memanggil API backend untuk membuat PDF
    try {
      // Ganti dengan URL backend Anda yang sebenarnya
      const response = await fetch('/api/generate-pdf', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dataToSend),
      });

      if (response.ok) {
        // Tampilkan modal sukses
        setShowSuccessModal(true);
        // Reset form
        setFormData({
          customer: { location: 'Wrap Station Medan', firstName: '', lastName: '', phone: '', carBrand: '', carModel: '', color: '' },
          inspection: {},
          signature: '',
          agreed: false,
        });
        setActiveTab('customer');
      } else {
        // Tangani kesalahan
        alert('Gagal membuat PDF. Silakan coba lagi.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Terjadi kesalahan koneksi.');
    } finally {
        setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      <Header />
      <main className="p-6">
        <div className="flex space-x-2 mb-6">
          <TabIcon
            Icon={CarIcon}
            active={activeTab === 'customer'}
            onClick={() => setActiveTab('customer')}
          />
          <TabIcon
            Icon={ClipboardIcon}
            active={activeTab === 'inspection'}
            onClick={() => setActiveTab('inspection')}
          />
          <TabIcon
            Icon={CheckSquareIcon}
            active={activeTab === 'signature'}
            onClick={() => setActiveTab('signature')}
          />
        </div>

        <div className="bg-white p-6 md:p-10 rounded-lg shadow-sm">
          {activeTab === 'customer' && (
            <CustomerForm
              data={formData.customer}
              onChange={(field, value) => handleFormChange('customer', field, value)}
            />
          )}
          {activeTab === 'inspection' && (
            <InspectionForm
              data={formData.inspection}
              onChange={(field, value) => handleFormChange('inspection', field, value)}
            />
          )}
          {activeTab === 'signature' && (
            <SignatureForm
              data={formData}
              onSignatureChange={(value) => handleFormChange('main', 'signature', value)}
              onAgreeChange={(value) => handleFormChange('main', 'agreed', value)}
              onSubmit={handleSubmit}
              isSubmitting={isSubmitting}
            />
          )}
        </div>
      </main>
      {showSuccessModal && <SuccessModal onClose={() => setShowSuccessModal(false)} />}
    </div>
  );
}

// --- Komponen Header ---
function Header() {
  return (
    <nav className="bg-white shadow-sm">
      <div className="container mx-auto px-6 py-3 flex justify-between items-center">
        <div className="flex items-center">
          <div className="bg-gray-800 text-white font-bold p-3 rounded-lg">
            WRAPSTATION
          </div>
          <div className="hidden md:flex items-center ml-10 space-x-1">
            <NavItem active>Dashboard</NavItem>
            <NavItemDropdown title="Activity" />
            <NavItem>Activity Report</NavItem>
            <NavItemDropdown title="Back Office" />
            <NavItemDropdown title="Finance" />
            <NavItemDropdown title="Database" />
          </div>
        </div>
        <div className="flex items-center">
          <button className="flex items-center bg-gray-900 text-white px-4 py-2 rounded-lg">
            <UserIcon className="w-5 h-5 mr-2" />
            <span>Agus</span>
            <ChevronDownIcon className="w-4 h-4 ml-1" />
          </button>
        </div>
      </div>
    </nav>
  );
}

const NavItem = ({ children, active = false }) => (
  <a
    href="#"
    className={`px-3 py-2 rounded-md text-sm font-medium ${
      active
        ? 'bg-gray-200 text-gray-900'
        : 'text-gray-700 hover:bg-gray-100'
    }`}
  >
    {children}
  </a>
);

const NavItemDropdown = ({ title }) => (
  <a
    href="#"
    className="px-3 py-2 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-100 flex items-center"
  >
    {title}
    <ChevronDownIcon className="w-4 h-4 ml-1" />
  </a>
);

// --- Komponen Tab Ikon ---
const TabIcon = ({ Icon, active, ...props }) => (
  <button
    {...props}
    className={`p-3 rounded-lg ${
      active
        ? 'bg-blue-600 text-white'
        : 'bg-white text-blue-600 shadow-sm'
    }`}
  >
    <Icon className="w-6 h-6" />
  </button>
);

// --- Komponen Form 1: Customer ---
function CustomerForm({ data, onChange }) {
  const fields = [
    { label: 'Location', key: 'location', disabled: true },
    { label: 'Customer Front Name', key: 'firstName', placeholder: 'Masukkan nama depan' },
    { label: 'Customer Last name', key: 'lastName', placeholder: 'Masukkan nama belakang' },
    { label: 'Customer Phone Number', key: 'phone', placeholder: 'Masukkan nomor telepon' },
    { label: 'Car Brand', key: 'carBrand', placeholder: 'Masukkan merek mobil' },
    { label: 'Car Model', key: 'carModel', placeholder: 'Masukkan model mobil' },
    { label: 'Color', key: 'color', placeholder: 'Masukkan warna mobil' },
  ];

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-2xl font-semibold mb-8 text-gray-800">Customer & Vehicle Details</h2>
