// --- Komponen Form 2: Inspection ---
const inspectionItems = {
  Paint: ['Paint'], // Tambahkan paint di sini sesuai gambar awal
  Glass: ['Windshield', 'Windows', 'Mirrors', 'Rear Window'],
  'Tires and Wheels': ['Tires', 'Wheels'],
  Exterior: ['Grille', 'Bumper (Front)', 'Bumper (Rear)'], // Contoh tambahan
};

function InspectionForm({ data, onChange }) {
  const [openNotes, setOpenNotes] = useState({});

  const handleRatingChange = (item, rating) => {
    onChange(item, { ...data[item], rating });
  };

  const handleNoteChange = (item, note) => {
    onChange(item, { ...data[item], note });
  };

  // Ubah ini untuk menyimpan objek File dan Data URL
  const handleFileChange = (item, file) => {
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        onChange(item, { ...data[item], file: { name: file.name, dataUrl: reader.result } });
      };
      reader.readAsDataURL(file); // Baca file sebagai Data URL
    } else {
      onChange(item, { ...data[item], file: undefined });
    }
  };


  const toggleNote = (item) => {
    setOpenNotes((prev) => ({ ...prev, [item]: !prev[item] }));
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-2xl font-semibold mb-8 text-gray-800">Vehicle Inspection</h2>
      <p className="text-sm text-gray-600 mb-6">
        G = Good (Baik), F = Fair (Cukup), P = Poor (Buruk)
      </p>

      {Object.entries(inspectionItems).map(([category, items]) => (
        <div key={category} className="mb-10">
          <h3 className="text-xl font-semibold text-gray-700 mb-6 border-b pb-2">
            {category}
          </h3>
          <div className="space-y-4">
            {items.map((item) => (
              <div key={item}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <RatingButtons
                      rating={data[item]?.rating}
                      onChange={(rating) => handleRatingChange(item, rating)}
                    />
                    <span className="text-md font-medium text-gray-800">
                      {item}
                    </span>
                  </div>
                  <button
                    onClick={() => toggleNote(item)}
                    className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg text-sm font-medium hover:bg-gray-300"
                  >
                    Note
                  </button>
                </div>
                {openNotes[item] && (
                  <NoteUploader
                    note={data[item]?.note || ''}
                    file={data[item]?.file?.name || ''} // Kirim hanya nama file untuk tampilan
                    onNoteChange={(note) => handleNoteChange(item, note)}
                    onFileChange={(file) => handleFileChange(item, file)}
                    previewUrl={data[item]?.file?.dataUrl} // Kirim data URL untuk preview
                  />
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

const RatingButtons = ({ rating, onChange }) => (
  <div className="flex bg-white border border-gray-300 rounded-lg p-1 space-x-1">
    {['G', 'F', 'P'].map((r) => (
      <button
        key={r}
        onClick={() => onChange(r)}
        className={`px-3 py-1 rounded text-sm font-bold ${
          rating === r
            ? 'bg-blue-600 text-white'
            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
        }`}
      >
        {r}
      </button>
    ))}
  </div>
);

function NoteUploader({ note, file, onNoteChange, onFileChange, previewUrl }) {
  const fileInputRef = useRef(null);

  const handleFile = (e) => {
    const file = e.target.files[0];
    onFileChange(file);
  };

  return (
    <div className="mt-4 p-4 bg-gray-50 rounded-lg border border-gray-200">
      <textarea
        value={note}
        onChange={(e) => onNoteChange(e.target.value)}
        placeholder="Tambahkan keterangan..."
        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 mb-3"
        rows="3"
      ></textarea>
      <div className="flex items-center">
        <button
          onClick={() => fileInputRef.current.click()}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg text-sm font-medium hover:bg-blue-700"
        >
          Choose Image
        </button>
        <span className="text-sm text-gray-600 ml-3">
          {file || 'No image chosen'}
        </span>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFile}
          className="hidden"
          accept="image/*"
        />
      </div>
      {previewUrl && (
        <div className="mt-4">
          <img src={previewUrl} alt="Preview" className="max-w-xs h-auto rounded-lg shadow-sm" />
        </div>
      )}
    </div>
  );
}

// --- Komponen Form 3: Signature ---
function SignatureForm({ data, onSignatureChange, onAgreeChange, onSubmit, isSubmitting }) {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.strokeStyle = '#000';
      ctx.lineWidth = 2;
    }
  }, []);

  const startDrawing = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing) return;
    const { offsetX, offsetY } = nativeEvent;
    const ctx = canvasRef.current.getContext('2d');
    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
  };

  const stopDrawing = () => {
    const ctx = canvasRef.current.getContext('2d');
    ctx.closePath();
    setIsDrawing(false);
    // Simpan sebagai data URL
    onSignatureChange(canvasRef.current.toDataURL());
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    onSignatureChange('');
  };

  const terms = [
    "Kondisi kendaraan dapat berubah setelah pembersihan.",
    "Status cat kendaraan (repaint/original) tidak dapat dipastikan.",
    "Penambahan jarak tempuh (mileage) bisa terjadi.",
    "Kerusakan/malfungsi mesin selama atau setelah inspeksi bukan tanggung jawab kami.",
    "Kehilangan barang pribadi bukan tanggung jawab kami.",
  ];

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">
        Syarat dan Ketentuan - Serah Terima Kendaraan
      </h2>
      <div className="space-y-2 text-gray-700 text-sm mb-6 p-4 bg-gray-50 rounded-lg border">
        {terms.map((term, index) => (
          <p key={index}>
            {index + 1}. {term}
          </p>
        ))}
      </div>
      <div className="flex items-center mb-6">
        <input
          type="checkbox"
          id="agree"
          checked={data.agreed}
          onChange={(e) => onAgreeChange(e.target.checked)}
          className="w-5 h-5 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500"
        />
        <label htmlFor="agree" className="ml-3 text-md font-medium text-gray-900">
          Saya telah membaca dan menyetujui Syarat & Ketentuan.
        </label>
      </div>

      <h3 className="text-xl font-semibold mb-4 text-gray-800">Signature</h3>
      <div className="border border-gray-400 rounded-lg bg-white w-full h-48 cursor-crosshair">
        <canvas
          ref={canvasRef}
          width="600" // Atur ukuran kanvas
          height="190" // Atur ukuran kanvas
          onMouseDown={startDrawing}
          onMouseMove={draw}
          onMouseUp={stopDrawing}
          onMouseLeave={stopDrawing}
        ></canvas>
      </div>
      <button
        onClick={clearCanvas}
        className="text-sm text-blue-600 hover:underline mt-2"
      >
        Clear Signature
      </button>

      <div className="mt-10 text-right">
        <button
          onClick={onSubmit}
          disabled={!data.agreed || !data.signature || isSubmitting}
          className="px-8 py-3 bg-blue-600 text-white rounded-lg font-semibold text-lg hover:bg-blue-700 disabled:bg-gray-400"
        >
          {isSubmitting ? 'Generating PDF...' : 'Submit Inspection'}
        </button>
      </div>
    </div>
  );
}

