import React, { useState, useCallback, useRef, useEffect } from 'react';
import { Truck, FileText, CheckSquare, X, Image as ImageIcon, Trash2, Loader, Send } from 'lucide-react';

// --- CONFIGURATION ---
const BACKEND_URL = '/api/generate-pdf'; 

// Status: G=Good, F=Fair, P=Poor
const INSPECTION_STATUS = [
  { value: 'G', label: 'G' },
  { value: 'F', label: 'F' },
  { value: 'P', label: 'P' },
];

const inspectionItems = [
  { category: 'Paint', items: ['Paint'] },
  { category: 'Glass', items: ['Windshield', 'Windows', 'Mirrors', 'Rear Window'] },
  { category: 'Tires and Wheels', items: ['Tires', 'Wheels'] },
  { category: 'Interior', items: ['Dashboard', 'Seats', 'Carpet'] },
];

// --- UTILITY COMPONENTS ---

const Modal = ({ title, children, onClose }) => (
  <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center p-4">
    <div className="bg-white rounded-xl shadow-2xl max-w-lg w-full p-6 space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-xl font-bold text-gray-800">{title}</h3>
        <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
          <X size={24} />
        </button>
      </div>
      <div className="modal-body">{children}</div>
    </div>
  </div>
);

const SuccessModal = ({ onClose }) => (
  <Modal title="Success" onClose={onClose}>
    <div className="flex flex-col items-center justify-center space-y-4">
      <div className="p-4 bg-green-100 rounded-full">
        <CheckSquare size={48} className="text-green-500" />
      </div>
      <p className="text-lg font-semibold text-gray-700">Inspection Successfully Submitted!</p>
      <p className="text-sm text-gray-500">Silakan cek file PDF yang terunduh.</p>
      <button 
        onClick={onClose} 
        className="px-6 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition duration-150 shadow-md"
      >
        OK
      </button>
    </div>
  </Modal>
);

const ErrorModal = ({ onClose, message }) => (
  <Modal title="Error" onClose={onClose}>
    <div className="flex flex-col items-center justify-center space-y-4">
      <div className="p-4 bg-red-100 rounded-full">
        <X size={48} className="text-red-500" />
      </div>
      <p className="text-lg font-semibold text-gray-700">Gagal Memproses Data</p>
      <p className="text-sm text-red-500">{message}</p>
      <button 
        onClick={onClose} 
        className="px-6 py-2 bg-red-600 text-white font-medium rounded-lg hover:bg-red-700 transition duration-150 shadow-md"
      >
        Tutup
      </button>
    </div>
  </Modal>
);

// --- FORM COMPONENTS ---

// Komponen untuk memilih status G/F/P
const StatusSelector = ({ section, item, value, onChange }) => {
  const isSelected = (status) => value === status;

  return (
    <div className="flex items-center space-x-2">
      {INSPECTION_STATUS.map((status) => (
        <button
          key={status.value}
          type="button"
          onClick={() => onChange(section, item, status.value)}
          className={`px-3 py-1 font-semibold rounded-lg text-sm transition duration-150 ${
            isSelected(status.value)
              ? 'bg-blue-600 text-white shadow-md'
              : 'bg-gray-200 text-gray-700 hover:bg-blue-100'
          }`}
        >
          {status.label}
        </button>
      ))}
    </div>
  );
};

// Komponen untuk input catatan dan gambar
const NoteUploader = ({ section, item, notes, file, handleInspectionChange }) => {
  const [isOpen, setIsOpen] = useState(false);
  const fileInputRef = useRef(null);

  const handleNoteChange = (e) => {
    handleInspectionChange(section, item, 'notes', e.target.value);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => {
        // Simpan Data URL (Base64) ke state
        handleInspectionChange(section, item, 'file', {
          name: selectedFile.name,
          dataUrl: reader.result,
        });
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleFileClear = () => {
    handleInspectionChange(section, item, 'file', null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <>
      <button
        type="button"
        onClick={() => setIsOpen(true)}
        className={`px-3 py-1 text-sm font-semibold rounded-lg transition duration-150 ${
          notes || file ? 'bg-yellow-500 text-white hover:bg-yellow-600' : 'bg-gray-300 text-gray-800 hover:bg-gray-400'
        }`}
      >
        Note
      </button>

      {isOpen && (
        <Modal title={`Notes & Image for ${item}`} onClose={() => setIsOpen(false)}>
          <div className="space-y-4">
            {/* Textarea untuk Catatan */}
            <label className="block text-sm font-medium text-gray-700">Catatan/Keterangan</label>
            <textarea
              value={notes}
              onChange={handleNoteChange}
              rows="3"
              className="w-full p-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
              placeholder={`Tambahkan catatan untuk ${item}...`}
            />

            {/* Input File Gambar */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-gray-700">Unggah Gambar</label>
              <div className="flex items-center space-x-3">
                <input
                  type="file"
                  accept="image/*"
                  ref={fileInputRef}
                  onChange={handleFileChange}
                  className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
                {file && (
                  <button onClick={handleFileClear} className="text-red-500 hover:text-red-700 p-2 rounded-full hover:bg-red-100 transition duration-150">
                    <Trash2 size={20} />
                  </button>
                )}
              </div>

              {/* Pratinjau Gambar */}
              {file && file.dataUrl && (
                <div className="mt-3 p-3 border border-gray-200 rounded-lg flex items-center space-x-3">
                  <ImageIcon size={20} className="text-blue-500" />
                  <span className="text-sm font-medium text-gray-800 truncate">{file.name}</span>
                  <p className="text-xs text-gray-500">(Preview di bawah)</p>
                </div>
              )}
              {file && file.dataUrl && (
                <img 
                  src={file.dataUrl} 
                  alt="Preview" 
                  className="mt-2 max-h-40 w-auto rounded-lg object-contain border border-gray-300" 
                />
              )}
            </div>

            <button
              onClick={() => setIsOpen(false)}
              className="w-full py-2 bg-green-600 text-white font-medium rounded-lg hover:bg-green-700 transition duration-150 mt-4"
            >
              Selesai
            </button>
          </div>
        </Modal>
      )}
    </>
  );
};

// --- STEP COMPONENTS ---

const CustomerDetailForm = ({ formData, handleFormChange }) => (
  <div className="space-y-4 p-6 bg-white rounded-xl shadow-lg">
    <h2 className="text-2xl font-bold text-gray-800 border-b pb-2 mb-4">Detail Pelanggan & Kendaraan</h2>
    <div className="grid md:grid-cols-2 gap-4">
      {/* Customer Details */}
      <div className="md:col-span-2">
        <label className="block text-sm font-medium text-gray-700">Lokasi</label>
        <input
          type="text"
          value={formData.customer.location}
          onChange={(e) => handleFormChange('customer', 'location', e.target.value)}
          placeholder="Wrap Station Medan"
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Nama Depan Pelanggan</label>
        <input
          type="text"
          value={formData.customer.firstName}
          onChange={(e) => handleFormChange('customer', 'firstName', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Nama Belakang Pelanggan</label>
        <input
          type="text"
          value={formData.customer.lastName}
          onChange={(e) => handleFormChange('customer', 'lastName', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      <div className="md:col-span-2">
        <label className="block text-sm font-medium text-gray-700">Nomor Telepon Pelanggan</label>
        <input
          type="tel"
          value={formData.customer.phone}
          onChange={(e) => handleFormChange('customer', 'phone', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      {/* Vehicle Details */}
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Merek Mobil</label>
        <input
          type="text"
          value={formData.customer.carBrand}
          onChange={(e) => handleFormChange('customer', 'carBrand', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Model Mobil</label>
        <input
          type="text"
          value={formData.customer.carModel}
          onChange={(e) => handleFormChange('customer', 'carModel', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Warna</label>
        <input
          type="text"
          value={formData.customer.color}
          onChange={(e) => handleFormChange('customer', 'color', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">Nomor Plat</label>
        <input
          type="text"
          value={formData.customer.plateNumber}
          onChange={(e) => handleFormChange('customer', 'plateNumber', e.target.value)}
          className="mt-1 block w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
    </div>
  </div>
);

const InspectionForm = ({ formData, handleInspectionChange }) => {
  return (
    <div className="space-y-8 p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 border-b pb-2 mb-4">Inspeksi Kendaraan</h2>
      <p className="text-sm text-gray-500 mb-6">
        <span className="font-bold">G</span> = Baik (Good), <span className="font-bold">F</span> = Cukup (Fair), <span className="font-bold">P</span> = Buruk (Poor)
      </p>

      {inspectionItems.map((group) => (
        <div key={group.category} className="border-t pt-4">
          <h3 className="text-xl font-semibold text-gray-700 mb-3">{group.category}</h3>
          <div className="space-y-4">
            {group.items.map((item) => (
              <div key={item} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg border">
                <div className="flex items-center space-x-4 w-3/5">
                  <StatusSelector
                    section="inspection"
                    item={item}
                    value={formData.inspection[item]?.status || ''}
                    onChange={(section, subitem, value) => handleInspectionChange(section, subitem, 'status', value)}
                  />
                  <span className="font-medium text-gray-800">{item}</span>
                </div>

                <div className="w-2/5 flex justify-end">
                  <NoteUploader
                    section="inspection"
                    item={item}
                    notes={formData.inspection[item]?.notes || ''}
                    file={formData.inspection[item]?.file || null}
                    handleInspectionChange={handleInspectionChange}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

const SignatureForm = ({ formData, handleFormChange, onSubmit, isLoading }) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);

  const drawSignature = useCallback((e, isTouch = false) => {
    if (!isDrawing) return;
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');

    // Hitung posisi relatif
    const rect = canvas.getBoundingClientRect();
    const x = isTouch ? e.touches[0].clientX - rect.left : e.clientX - rect.left;
    const y = isTouch ? e.touches[0].clientY - rect.top : e.clientY - rect.top;

    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    ctx.strokeStyle = '#000000';
    ctx.lineTo(x, y);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(x, y);
  }, [isDrawing]);

  const handleStart = useCallback((e, isTouch) => {
    setIsDrawing(true);
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');

    // Hitung posisi relatif
    const rect = canvas.getBoundingClientRect();
    const x = isTouch ? e.touches[0].clientX - rect.left : e.clientX - rect.left;
    const y = isTouch ? e.touches[0].clientY - rect.top : e.clientY - rect.top;

    ctx.beginPath();
    ctx.moveTo(x, y);
    // Tambahkan titik tunggal jika hanya klik/tap
    ctx.lineTo(x, y); 
    ctx.stroke();
  }, []);

  const handleEnd = useCallback(() => {
    setIsDrawing(false);
    const canvas = canvasRef.current;
    if (canvas) {
      // Simpan tanda tangan sebagai Data URL (Base64)
      const dataUrl = canvas.toDataURL('image/png');
      handleFormChange('root', 'signature', dataUrl);
    }
  }, [handleFormChange]);

  const clearSignature = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      handleFormChange('root', 'signature', ''); // Kosongkan state
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      // Set canvas resolution based on display size for clear rendering
      const rect = canvas.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;

      // Handle Mouse Events
      canvas.addEventListener('mousedown', (e) => handleStart(e, false));
      canvas.addEventListener('mousemove', (e) => drawSignature(e, false));
      document.addEventListener('mouseup', handleEnd); // Use document for mouseup outside canvas

      // Handle Touch Events (for mobile responsiveness)
      canvas.addEventListener('touchstart', (e) => handleStart(e, true));
      canvas.addEventListener('touchmove', (e) => drawSignature(e, true));
      canvas.addEventListener('touchend', handleEnd);
    }

    // Cleanup event listeners
    return () => {
      if (canvas) {
        canvas.removeEventListener('mousedown', (e) => handleStart(e, false));
        canvas.removeEventListener('mousemove', (e) => drawSignature(e, false));
        document.removeEventListener('mouseup', handleEnd);
        canvas.removeEventListener('touchstart', (e) => handleStart(e, true));
        canvas.removeEventListener('touchmove', (e) => drawSignature(e, true));
        canvas.removeEventListener('touchend', handleEnd);
      }
    };
  }, [handleStart, drawSignature, handleEnd]);


  const isSubmitDisabled = !formData.agreed || !formData.signature;

  const onAgreeChange = (e) => {
    // Memperbarui state 'agreed' di root formData
    handleFormChange('root', 'agreed', e.target.checked);
  };

  return (
    <div className="space-y-6 p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 border-b pb-2 mb-4">Syarat & Tanda Tangan</h2>

      {/* Syarat dan Ketentuan (Placeholder) */}
      <div className="text-sm text-gray-600 space-y-2 p-4 border border-gray-200 rounded-lg max-h-64 overflow-y-auto">
        <h3 className="font-semibold text-lg text-gray-800 mb-2">Syarat dan Ketentuan - Serah Terima Kendaraan di Wrap Station</h3>
        <ol className="list-decimal list-inside space-y-1">
          <li>Kondisi kendaraan dapat berubah setelah pembersihan. Tim akan menginformasikan jika ada perubahan.</li>
          <li>Status cat kendaraan (repaint/original) tidak dapat dipastikan, risiko ditanggung pemilik.</li>
          <li>Penambahan jarak tempuh (mileage) bisa terjadi, dan bukan tanggung jawab Wrap Station.</li>
          <li>Kerusakan/malfungsi mesin selama atau setelah pengerjaan bukan tanggung jawab kami.</li>
          <li>Kerusakan akibat pembongkaran aksesori oleh pihak ketiga bukan tanggung jawab kami.</li>
          <li>Kehilangan barang pribadi bukan tanggung jawab kami.</li>
          <li>Wrap Station berhak melakukan tindakan teknis yang diperlukan tanpa persetujuan lebih lanjut.</li>
          <li>Kondisi/modifikasi khusus yang tidak diinformasikan di awal dapat membatalkan garansi.</li>
          <li>Penurunan baterai EV adalah kondisi alami, bukan tanggung jawab kami.</li>
          <li>Estimasi pengerjaan dapat berubah karena faktor tak terduga.</li>
        </ol>
      </div>

      {/* Checkbox Persetujuan */}
      <div className="flex items-start pt-4 border-t">
        <input
          type="checkbox"
          checked={formData.agreed}
          onChange={onAgreeChange} // Gunakan handler yang telah diperbaiki
          id="agreement-checkbox"
          className="mt-1 w-5 h-5 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
        />
        <label htmlFor="agreement-checkbox" className="ml-3 text-base font-medium text-gray-800 select-none">
          Saya telah membaca dan menyetujui Syarat & Ketentuan.
        </label>
      </div>

      {/* Tanda Tangan */}
      <div className="pt-4">
        <h3 className="text-lg font-bold text-gray-800 mb-2">Tanda Tangan Pelanggan</h3>
        <div className="relative border-2 border-gray-300 rounded-lg overflow-hidden bg-white">
          <canvas 
            ref={canvasRef} 
            className="w-full h-48 cursor-crosshair bg-white" 
            style={{ touchAction: 'none' }} // Mencegah scrolling pada touch device saat menggambar
          />
          <button
            type="button"
            onClick={clearSignature}
            className="absolute top-2 right-2 p-1 bg-red-100 text-red-600 rounded-full shadow-md hover:bg-red-200 transition duration-150"
            title="Hapus Tanda Tangan"
          >
            <Trash2 size={20} />
          </button>
        </div>
      </div>

      {/* Tombol Submit */}
      <button
        type="button"
        onClick={onSubmit}
        disabled={isSubmitDisabled || isLoading}
        className={`w-full py-3 mt-6 flex items-center justify-center space-x-2 rounded-lg text-lg font-semibold transition duration-200 ${
          isSubmitDisabled || isLoading
            ? 'bg-gray-400 text-gray-600 cursor-not-allowed'
            : 'bg-green-600 text-white hover:bg-green-700 shadow-xl'
        }`}
      >
        {isLoading ? (
          <>
            <Loader size={20} className="animate-spin" />
            <span>Membuat PDF...</span>
          </>
        ) : (
          <>
            <Send size={20} />
            <span>Submit Inspection & Download PDF</span>
          </>
        )}
      </button>
      {isSubmitDisabled && (
        <p className="text-sm text-center text-red-500">Anda harus menyetujui Syarat & Ketentuan dan memberikan Tanda Tangan untuk Submit.</p>
      )}
    </div>
  );
};


// --- MAIN APP COMPONENT ---

const App = () => {
  const [step, setStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [showModal, setShowModal] = useState(null); // 'success' or 'error'
  const [errorMessage, setErrorMessage] = useState('');

  const [formData, setFormData] = useState(() => {
    // Inisialisasi state awal
    const initialInspection = inspectionItems.flatMap(group => group.items).reduce((acc, item) => {
      acc[item] = { status: '', notes: '', file: null };
      return acc;
    }, {});

    return {
      customer: {
        location: 'Wrap Station Medan',
        firstName: '',
        lastName: '',
        phone: '',
        carBrand: '',
        carModel: '',
        color: '',
        plateNumber: '',
      },
      inspection: initialInspection,
      signature: '',
      agreed: false,
    };
  });

  // Handler untuk Customer Form
  const handleFormChange = (section, field, value) => {
    // Perbaikan: Menangani pembaruan pada level root ('signature' dan 'agreed')
    if (section === 'root') {
      setFormData((prev) => ({
        ...prev,
        [field]: value,
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [field]: value,
        },
      }));
    }
  };

  // Handler untuk Inspection Form (bertingkat: inspection.item.status/notes/file)
  const handleInspectionChange = useCallback((section, item, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: {
        ...prev[section],
        [item]: {
          ...prev[section][item],
          [field]: value,
        },
      },
    }));
  }, []);

  const handleSubmit = async () => {
    if (!formData.agreed || !formData.signature) {
      setErrorMessage('Harap setujui Syarat & Ketentuan dan berikan Tanda Tangan.');
      setShowModal('error');
      return;
    }

    setIsLoading(true);
    setErrorMessage('');

    try {
      // 1. Filter Data URL Gambar yang valid dan hapus input file mentah
      const dataToSend = JSON.parse(JSON.stringify(formData));

      // Hapus Data URL yang besar jika tidak ada, atau saring hanya yang dibutuhkan
      Object.keys(dataToSend.inspection).forEach(item => {
        if (dataToSend.inspection[item].file) {
          // Pastikan hanya mengirim name dan dataUrl
          dataToSend.inspection[item].file = {
            name: dataToSend.inspection[item].file.name,
            dataUrl: dataToSend.inspection[item].file.dataUrl
          };
        }
      });

      // 2. Panggil API Backend
      const response = await fetch(BACKEND_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataToSend),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status} ${response.statusText}`);
      }

      // 3. Terima Blob PDF dan Unduh
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;

      const fileName = `Inspection_${formData.customer.carBrand}_${formData.customer.plateNumber}_${new Date().toISOString().slice(0, 10)}.pdf`;
      a.download = fileName;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);

      setShowModal('success');
      // Tidak perlu reset form, biarkan data tetap ada setelah sukses

    } catch (error) {
      console.error('Submission Error:', error);
      setErrorMessage(`Gagal menghubungi server atau memproses PDF: ${error.message}`);
      setShowModal('error');
    } finally {
      setIsLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (step) {
      case 1:
        return <CustomerDetailForm formData={formData} handleFormChange={handleFormChange} />;
      case 2:
        return <InspectionForm formData={formData} handleInspectionChange={handleInspectionChange} />;
      case 3:
        return <SignatureForm formData={formData} handleFormChange={handleFormChange} onSubmit={handleSubmit} isLoading={isLoading} />;
      default:
        return <CustomerDetailForm formData={formData} handleFormChange={handleFormChange} />;
    }
  };

  const isNextDisabled = () => {
    if (step === 1) {
      // Pastikan semua field customer/vehicle terisi
      const required = ['firstName', 'phone', 'carBrand', 'carModel', 'plateNumber'];
      return required.some(field => !formData.customer[field]);
    }
    // Tidak ada validasi ketat untuk Step 2 (Inspection)
    return false;
  };

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      {/* HEADER / NAVIGATION BAR (Dibuat sederhana) */}
      <header className="bg-white shadow-md sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-extrabold text-blue-700">WRAP STATION</h1>
          <div className="flex items-center space-x-4">
            <span className="text-gray-700 font-medium">Inspeksi Kendaraan</span>
            <div className="flex items-center space-x-2 bg-gray-100 p-2 rounded-full">
               <span className="text-sm text-gray-500">User: Agus</span>
               <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            </div>
          </div>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <main className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* STEPPER / ICONS */}
        <div className="flex justify-center space-x-8 mb-8 p-4 bg-white rounded-xl shadow-lg">
          <StepIcon step={1} currentStep={step} label="Customer" Icon={Truck} setStep={setStep} isDisabled={false} />
          <StepIcon step={2} currentStep={step} label="Inspection" Icon={FileText} setStep={setStep} isDisabled={isNextDisabled() && step < 2} />
          <StepIcon step={3} currentStep={step} label="Syarat & TTD" Icon={CheckSquare} setStep={setStep} isDisabled={false} />
        </div>

        {/* FORM CONTENT */}
        <div className="transition-opacity duration-300">
          {renderStepContent()}
        </div>

        {/* NAVIGATION BUTTONS */}
        <div className="flex justify-between mt-8">
          <button
            onClick={() => setStep(prev => Math.max(1, prev - 1))}
            disabled={step === 1 || isLoading}
            className={`px-6 py-3 rounded-lg text-lg font-semibold transition duration-200 shadow-md ${
              step === 1 || isLoading
                ? 'bg-gray-300 text-gray-600 cursor-not-allowed'
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            &larr; Sebelumnya
          </button>

          {step < 3 && (
            <button
              onClick={() => setStep(prev => Math.min(3, prev + 1))}
              disabled={isNextDisabled() || isLoading}
              className={`px-6 py-3 rounded-lg text-lg font-semibold transition duration-200 shadow-md ${
                isNextDisabled() || isLoading
                  ? 'bg-gray-300 text-gray-600 cursor-not-allowed'
                  : 'bg-blue-600 text-white hover:bg-blue-700'
              }`}
            >
              Selanjutnya &rarr;
            </button>
          )}
        </div>
      </main>

      {/* MODALS */}
      {showModal === 'success' && <SuccessModal onClose={() => setShowModal(null)} />}
      {showModal === 'error' && <ErrorModal onClose={() => setShowModal(null)} message={errorMessage} />}
    </div>
  );
};

const StepIcon = ({ step, currentStep, label, Icon, setStep, isDisabled }) => {
    const isActive = step === currentStep;
    const isCompleted = step < currentStep;

    let buttonClass = 'bg-gray-300 text-gray-600 cursor-not-allowed';
    let iconClass = 'text-gray-500';

    if (isActive) {
        buttonClass = 'bg-blue-600 text-white shadow-xl';
        iconClass = 'text-white';
    } else if (isCompleted) {
        buttonClass = 'bg-green-500 text-white hover:bg-green-600';
        iconClass = 'text-white';
    } else if (!isDisabled) {
        buttonClass = 'bg-gray-200 text-gray-700 hover:bg-gray-300';
        iconClass = 'text-gray-700';
    }

    return (
        <button
            onClick={() => setStep(step)}
            disabled={isDisabled || isActive}
            className={`flex flex-col items-center space-y-2 p-3 rounded-xl transition duration-300 ${isDisabled ? 'opacity-50' : ''}`}
        >
            <div className={`p-3 rounded-full ${buttonClass}`}>
                <Icon size={24} className={iconClass} />
            </div>
            <span className={`text-sm font-medium ${isActive ? 'text-blue-600 font-bold' : 'text-gray-600'}`}>
                {label}
            </span>
        </button>
    );
};

export default App;
